import{_ as a,c as e,o as s,a as n}from"./app.04c88653.js";const C=JSON.parse('{"title":"Welcome to Vascular Platform","description":"","frontmatter":{},"headers":[{"level":2,"title":"1. Register","slug":"_1-register","link":"#_1-register","children":[]},{"level":2,"title":"2. Sign In","slug":"_2-sign-in","link":"#_2-sign-in","children":[]},{"level":2,"title":"3. Running Vascular","slug":"_3-running-vascular","link":"#_3-running-vascular","children":[{"level":3,"title":"3.1 Prerequisites","slug":"_3-1-prerequisites","link":"#_3-1-prerequisites","children":[]},{"level":3,"title":"3.2 Pull the Image from the Registry","slug":"_3-2-pull-the-image-from-the-registry","link":"#_3-2-pull-the-image-from-the-registry","children":[]},{"level":3,"title":"3.3 Activating the Application","slug":"_3-3-activating-the-application","link":"#_3-3-activating-the-application","children":[]}]},{"level":2,"title":"4. Client Integration","slug":"_4-client-integration","link":"#_4-client-integration","children":[{"level":3,"title":"4.1 Download Client Plugins","slug":"_4-1-download-client-plugins","link":"#_4-1-download-client-plugins","children":[]}]}],"relativePath":"index.md"}'),l={name:"index.md"},t=n(`<h1 id="welcome-to-vascular-platform" tabindex="-1">Welcome to Vascular Platform <a class="header-anchor" href="#welcome-to-vascular-platform" aria-hidden="true">#</a></h1><p>Thank you for choosing Vascular. This guide will walk you through registration, setup, and integration.</p><hr><h2 id="_1-register" tabindex="-1">1. Register <a class="header-anchor" href="#_1-register" aria-hidden="true">#</a></h2><p>To get started, please <a href="https://yourdomain.com/register" target="_blank" rel="noreferrer">register an account</a>.</p><hr><h2 id="_2-sign-in" tabindex="-1">2. Sign In <a class="header-anchor" href="#_2-sign-in" aria-hidden="true">#</a></h2><p>If you already have an account, <a href="https://yourdomain.com/signin" target="_blank" rel="noreferrer">sign in here</a>.</p><hr><h2 id="_3-running-vascular" tabindex="-1">3. Running Vascular <a class="header-anchor" href="#_3-running-vascular" aria-hidden="true">#</a></h2><p>Before running Vascular, ensure you meet the following requirements.</p><h3 id="_3-1-prerequisites" tabindex="-1">3.1 Prerequisites <a class="header-anchor" href="#_3-1-prerequisites" aria-hidden="true">#</a></h3><p>Before you begin, make sure you have:</p><ul><li>An active subscription</li><li>Access credentials for our private container registry<br><em>(Vascular is distributed as a container image.)</em></li><li>A valid license file (<code>license.key</code>)<br> You can download this from your Customer Dashboard after registration and payment</li></ul><hr><h3 id="_3-2-pull-the-image-from-the-registry" tabindex="-1">3.2 Pull the Image from the Registry <a class="header-anchor" href="#_3-2-pull-the-image-from-the-registry" aria-hidden="true">#</a></h3><p>Use the following command to pull the latest version of the Vascular container:</p><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki"><code><span class="line"><span style="color:#A6ACCD;">docker pull vascular.registry.com/inbox:latest</span></span>
<span class="line"></span></code></pre></div><h3 id="_3-3-activating-the-application" tabindex="-1">3.3 Activating the Application <a class="header-anchor" href="#_3-3-activating-the-application" aria-hidden="true">#</a></h3><p>The application requires the license file to start. You can provide the license file in two ways:</p><h4 id="_1-default-location" tabindex="-1">1. Default location <a class="header-anchor" href="#_1-default-location" aria-hidden="true">#</a></h4><p>Mount the license file inside the container at:</p><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki"><code><span class="line"><span style="color:#A6ACCD;">/etc/vascular-inbox/license.key</span></span>
<span class="line"></span></code></pre></div><h4 id="_2-custom-location" tabindex="-1">2. Custom location <a class="header-anchor" href="#_2-custom-location" aria-hidden="true">#</a></h4><p>Mount the license file anywhere you prefer and provide the path using:</p><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki"><code><span class="line"><span style="color:#A6ACCD;">--license-file=/custom/path/license.key</span></span>
<span class="line"></span></code></pre></div><p>If the license file is missing or invalid, the application will not start.</p><p>** Example: Kubernetes Deployment with Custom License File Path **</p><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki"><code><span class="line"><span style="color:#A6ACCD;">apiVersion: apps/v1</span></span>
<span class="line"><span style="color:#A6ACCD;">kind: Deployment</span></span>
<span class="line"><span style="color:#A6ACCD;">metadata:</span></span>
<span class="line"><span style="color:#A6ACCD;">  name: vascular-inbox</span></span>
<span class="line"><span style="color:#A6ACCD;">spec:</span></span>
<span class="line"><span style="color:#A6ACCD;">  replicas: 1</span></span>
<span class="line"><span style="color:#A6ACCD;">  selector:</span></span>
<span class="line"><span style="color:#A6ACCD;">    matchLabels:</span></span>
<span class="line"><span style="color:#A6ACCD;">      app: vascular-inbox</span></span>
<span class="line"><span style="color:#A6ACCD;">  template:</span></span>
<span class="line"><span style="color:#A6ACCD;">    metadata:</span></span>
<span class="line"><span style="color:#A6ACCD;">      labels:</span></span>
<span class="line"><span style="color:#A6ACCD;">        app: vascular-inbox</span></span>
<span class="line"><span style="color:#A6ACCD;">    spec:</span></span>
<span class="line"><span style="color:#A6ACCD;">      containers:</span></span>
<span class="line"><span style="color:#A6ACCD;">        - name: vascular-inbox</span></span>
<span class="line"><span style="color:#A6ACCD;">          image: vascular.registry.com/inbox:latest</span></span>
<span class="line"><span style="color:#A6ACCD;">          args: [&quot;--license-file=/etc/vascular-inbox/license.key&quot;] # optional if using default</span></span>
<span class="line"><span style="color:#A6ACCD;">          volumeMounts:</span></span>
<span class="line"><span style="color:#A6ACCD;">            - name: license</span></span>
<span class="line"><span style="color:#A6ACCD;">              mountPath: /etc/vascular-inbox/license.key</span></span>
<span class="line"><span style="color:#A6ACCD;">              subPath: license.key</span></span>
<span class="line"><span style="color:#A6ACCD;">              readOnly: true</span></span>
<span class="line"><span style="color:#A6ACCD;">      volumes:</span></span>
<span class="line"><span style="color:#A6ACCD;">        - name: license</span></span>
<span class="line"><span style="color:#A6ACCD;">          secret:</span></span>
<span class="line"><span style="color:#A6ACCD;">            secretName: vascular-license</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div><h2 id="_4-client-integration" tabindex="-1">4. Client Integration <a class="header-anchor" href="#_4-client-integration" aria-hidden="true">#</a></h2><p>You can integrate Vascular with your client applications using one of our open-source plugins.</p><h3 id="_4-1-download-client-plugins" tabindex="-1">4.1 Download Client Plugins <a class="header-anchor" href="#_4-1-download-client-plugins" aria-hidden="true">#</a></h3><p>Web (React) <a href="https://github.com/vascular-io/vascular-js" target="_blank" rel="noreferrer">https://github.com/vascular-io/vascular-js</a></p><p>React Native <a href="https://github.com/vascular-io/vascular-js" target="_blank" rel="noreferrer">https://github.com/vascular-io/vascular-js</a></p><p>Android (Java/Kotlin) <a href="https://github.com/vascular-io/vascular-kotlin" target="_blank" rel="noreferrer">https://github.com/vascular-io/vascular-kotlin</a></p><p>iOS (Swift) <a href="https://github.com/vascular-io/vascular-swift" target="_blank" rel="noreferrer">https://github.com/vascular-io/vascular-swift</a></p><p>Flutter <a href="https://github.com/vascular-io/vascular-flutter" target="_blank" rel="noreferrer">https://github.com/vascular-io/vascular-flutter</a></p>`,37),i=[t];function r(o,c,p,u,h,d){return s(),e("div",null,i)}const m=a(l,[["render",r]]);export{C as __pageData,m as default};
